import styled from 'styled-components';

export default  styled.button`
font-family: sans:serif;
font-size: 1.3rem;
border: none;
border-radius: 5px;
padding: 1px 2px;
background: grey;
color: '#fff';
&:hover{
  background: blue;
}
`
